Entry.create([
  { :feed_id => 1, :title => "‘LOVEINT’", :url => "http://blogs.wsj.com/washwire/2013/08/23/nsa-officers-sometimes-spy-on-love-interests/", :author => "John Gruber", :summary => "The biggest problem with the NSA scandal is the lack of accountability. ★", :content => "<p>The biggest problem with the NSA scandal is the lack of accountability.</p>\n\n<div>\n<a  title=\"Permanent link to ‘&#8216;LOVEINT&#8217;’\"  href=\"http://daringfireball.net/linked/2013/08/23/loveint\">&nbsp;★&nbsp;</a>\n</div>", :published => "2013-08-24 01:27:10", :updated => "2013-08-24 01:31:22", :created_at => "2013-08-24 12:41:15", :updated_at => "2013-08-24 12:41:15", :entry_id => "tag:daringfireball.net,2013:/linked//6.28162", :public_id => "c59fc9641caf56dccceee33d2fd3d9a2b07d02c5", :old_public_id => "c59fc9641caf56dccceee33d2fd3d9a2b07d02c5", :starred_entries_count => 0 },
  { :feed_id => 1, :title => "Why Steve Ballmer Should Have Been Shitcanned No Later Than 2009", :url => "http://daringfireball.net/linked/2012/02/06/ballmer", :author => "John Gruber", :summary => "Another one on Ballmer from the archive: The damning thing isn’t that Apple got...", :content => "<p>Another one on Ballmer from the archive:</p>\n\n<blockquote>\n  <p>The damning thing isn’t that Apple got there first; it’s that\neven after Apple revealed it, that Ballmer didn’t get it, that\nhe didn’t see instantly that Apple had unveiled something\namazing and transformative. All Ballmer could see was the near\nfuture, the next few months where the iPhone was indeed too\nexpensive and where typing on a touchscreen was a novelty.</p>\n</blockquote>\n\n<div>\n<a  title=\"Permanent link to ‘Why Steve Ballmer Should Have Been Shitcanned No Later Than 2009’\"  href=\"http://daringfireball.net/linked/2013/08/23/ballmer-laughs\">&nbsp;★&nbsp;</a>\n</div>", :published => "2013-08-23 21:55:11", :updated => "2013-08-23 21:55:13", :created_at => "2013-08-24 12:41:15", :updated_at => "2013-08-24 12:41:15", :entry_id => "tag:daringfireball.net,2013:/linked//6.28161", :public_id => "12790d8faf543ae65ab8a7073257e8ce953c9ce6", :old_public_id => "12790d8faf543ae65ab8a7073257e8ce953c9ce6", :starred_entries_count => 0 },
  { :feed_id => 1, :title => "Microsoft Forces Steve Ballmer to Resign", :url => "http://www.microsoft.com/en-us/news/press/2013/aug13/08-23StatementPR.aspx", :author => "John Gruber", :summary => "Officially he’s “retiring within the next 12 months”, but that’s just framing to...", :content => "<p>Officially he&#8217;s &#8220;retiring within the next 12 months&#8221;, but that&#8217;s just framing to allow Ballmer and Microsoft itself to save some face. This is the axe, and it was <a href=\"http://daringfireball.net/2004/10/some_joke\">long</a> overdue. Ballmer has been a successful steward growing profits from the franchises he inherited from the Gates era &#8212; Windows and Office. But he&#8217;s been an abject failure at developing anything new. Under his watch Windows has been supplanted by Apple&#8217;s iOS and Google&#8217;s Android. Mobile is the industry&#8217;s growth area, and Microsoft is barely a player.</p>\n\n<p>Here&#8217;s the hitch though: Ballmer has chased all potential successors out of the company &#8212; Ray Ozzie, Robbie Bach, J Allard, and most recently, Steven Sinofsky.</p>\n\n<div>\n<a  title=\"Permanent link to ‘Microsoft Forces Steve Ballmer to Resign’\"  href=\"http://daringfireball.net/linked/2013/08/23/ballmer\">&nbsp;★&nbsp;</a>\n</div>", :published => "2013-08-23 21:02:32", :updated => "2013-08-23 21:46:43", :created_at => "2013-08-24 12:41:15", :updated_at => "2013-08-24 12:41:15", :entry_id => "tag:daringfireball.net,2013:/linked//6.28159", :public_id => "2b7e7709be0fdfe55e6ce8bc4aaa552736915c1a", :old_public_id => "2b7e7709be0fdfe55e6ce8bc4aaa552736915c1a", :starred_entries_count => 0 },
  { :feed_id => 1, :title => "Google Designing Its Own Self-Driving Car", :url => "http://jessicalessin.com/2013/08/23/exclusive-google-designing-its-own-self-driving-car-considers-robo-taxi-2/", :author => "John Gruber", :summary => "Amir Efrati: Google Inc., which has been working on software to help major...", :content => "<p>Amir Efrati:</p>\n\n<blockquote>\n  <p>Google Inc., which has been working on software to help major automakers build self-driving cars, also is quietly going around them by designing and developing a full-fledged self-driving car, according to people familiar with the matter.</p>\n</blockquote>\n\n<p>They&#8217;ve got to be eyeing Tesla, right?</p>\n\n<div>\n<a  title=\"Permanent link to ‘Google Designing Its Own Self-Driving Car’\"  href=\"http://daringfireball.net/linked/2013/08/23/google-car\">&nbsp;★&nbsp;</a>\n</div>", :published => "2013-08-23 20:58:23", :updated => "2013-08-23 21:00:38", :created_at => "2013-08-24 12:41:15", :updated_at => "2013-08-24 12:41:15", :entry_id => "tag:daringfireball.net,2013:/linked//6.28158", :public_id => "261b752261eebccf1d9be77dd86875b3ae219914", :old_public_id => "261b752261eebccf1d9be77dd86875b3ae219914", :starred_entries_count => 0 },
  { :feed_id => 1, :title => "Why We Should Care About Ichiro’s 4,000th Pro Hit", :url => "http://nymag.com/daily/intelligencer/2013/08/why-we-should-care-about-4000th-ichiro-pro-hit.html", :author => "John Gruber", :summary => "Can’t wait to see him win an improbable World Series title in two months. ★", :content => "<p>Can&#8217;t wait to see him win an improbable World Series title in two months.</p>\n\n<div>\n<a  title=\"Permanent link to ‘Why We Should Care About Ichiro&#8217;s 4,000th Pro Hit’\"  href=\"http://daringfireball.net/linked/2013/08/22/ichiro\">&nbsp;★&nbsp;</a>\n</div>", :published => "2013-08-22 23:21:08", :updated => "2013-08-23 00:42:18", :created_at => "2013-08-24 12:41:15", :updated_at => "2013-08-24 12:41:15", :entry_id => "tag:daringfireball.net,2013:/linked//6.28157", :public_id => "47340ccd30eda2ac8ba324c4cbcebdafd3fcd945", :old_public_id => "47340ccd30eda2ac8ba324c4cbcebdafd3fcd945", :starred_entries_count => 0 },
  { :feed_id => 1, :title => "Reuters Piece on Tim Cook and Employee Retention", :url => "http://www.reuters.com/article/2013/08/22/us-usa-apple-cook-insight-idUSBRE97L08B20130822", :author => "John Gruber", :summary => "Poornima Gupta and Peter Henderson, reporting for Reuters, on retention...", :content => "<p>Poornima Gupta and Peter Henderson, reporting for Reuters, on retention problems:</p>\n\n<blockquote>\n  <p>Some Silicon Valley recruiters and former Apple employees at rival\ncompanies say they are seeing more Apple resumes than ever before,\nespecially from hardware engineers, though the depth and breadth\nof any brain-drain remains difficult to quantify, especially given\nthe recent expansion in staff numbers.</p>\n\n<p>&#8220;I am being inundated by LinkedIn messages and emails both by\npeople who I never imagined would leave Apple and by people who\nhave been at Apple for a year, and who joined expecting something\ndifferent than what they encountered,&#8221; said one recruiter with\nties to Apple.</p>\n\n<p>Still, the Cook regime is also seen as kinder and gentler, and\nthat&#8217;s been a welcome change for many.</p>\n\n<p>&#8220;It is not as crazy as it used to be. It is not as draconian,&#8221;\nsaid Beth Fox, a recruiting consultant and former Apple employee,\nadding that the people she knows are staying put. &#8220;They like Tim.\nThey tend to err on the optimistic side.&#8221;</p>\n</blockquote>\n\n<p>So engineers are leaving in droves because Apple is a nicer place to work now?</p>\n\n<p>No doubt about it, <a href=\"http://daringfireball.net/linked/2013/03/08/the-talk-show-033\">retention is a key concern</a> for Apple, but they do not have a retention problem. I&#8217;d wager Apple has a higher retention rate than any of its Valley competitors. There may well be more Apple resumes in circulation than ever before, but there are more Apple employees than ever before &#8212; Apple has never been bigger than it is now, and Apple employees have never been in higher demand.</p>\n\n<blockquote>\n  <p>Still, employees report some grumbling, and Apple seems to have\ntaken note, conducting a survey of morale in the critical hardware\nengineering unit earlier this year.</p>\n\n<p>&#8220;As our business continues to grow and face new challenges, it\nbecomes increasingly important to get feedback about your\nperceptions and experiences working in hardware engineering,&#8221; Dan\nRiccio, Apple&#8217;s senior vice president of Hardware Engineering,\nwrote to his team in February in an email seen by Reuters.</p>\n</blockquote>\n\n<p>Apple does these surveys among employees every two or three years, and has done so throughout the modern era. I don&#8217;t think the survey cited above was in response to a rise in discontent.</p>\n\n<div>\n<a  title=\"Permanent link to ‘Reuters Piece on Tim Cook and Employee Retention’\"  href=\"http://daringfireball.net/linked/2013/08/22/reuters-cook\">&nbsp;★&nbsp;</a>\n</div>", :published => "2013-08-22 18:24:01", :updated => "2013-08-22 18:24:02", :created_at => "2013-08-24 12:41:15", :updated_at => "2013-08-24 12:41:15", :entry_id => "tag:daringfireball.net,2013:/linked//6.28155", :public_id => "7914f0d522563c7b93f34a66022850a0050b640f", :old_public_id => "7914f0d522563c7b93f34a66022850a0050b640f", :starred_entries_count => 0 },
  { :feed_id => 1, :title => "Glassboard Seeks New Home", :url => "http://inessential.com/2013/08/22/glassboard_seeks_new_home", :author => "John Gruber", :summary => "Brent Simmons: I don’t have any business relationship with Glassboard (or with...", :content => "<p>Brent Simmons:</p>\n\n<blockquote>\n  <p>I don’t have any business relationship with Glassboard (or with NewsGator or Sepia Labs), and so the only benefit I get from helping find Glassboard a new home is the selfish one: I use Glassboard every day and want to keep using it. (Q Branch uses it; my podcast uses it; my family uses it; the Seattle Xcoders group uses it.)</p>\n\n<p>The problem of persistent, private, and trustworthy group sharing is still an open problem. Glassboard represents a couple years of work by a six-person team, and it’s a great start. I believe that it can be very successful, given the right home, given resources and commitment.</p>\n</blockquote>\n\n<p>Great opportunity here for someone; Glassboard is a great product.</p>\n\n<div>\n<a  title=\"Permanent link to ‘Glassboard Seeks New Home’\"  href=\"http://daringfireball.net/linked/2013/08/22/glassboard\">&nbsp;★&nbsp;</a>\n</div>", :published => "2013-08-22 21:14:35", :updated => "2013-08-22 21:17:26", :created_at => "2013-08-24 12:41:15", :updated_at => "2013-08-24 12:41:15", :entry_id => "tag:daringfireball.net,2013:/linked//6.28156", :public_id => "3c09de40c7c9f47ff0d37f14d8ffed061857db13", :old_public_id => "3c09de40c7c9f47ff0d37f14d8ffed061857db13", :starred_entries_count => 1 },
  { :feed_id => 1, :title => "Poll: Louisiana GOPers Unsure if Katrina Response Was Obama’s Fault", :url => "http://livewire.talkingpointsmemo.com/entry/poll-louisiana-gopers-unsure-if-katrina-response-was", :author => "John Gruber", :summary => "Another classic from The Onion. Wait, what? ★", :content => "<p>Another classic from The Onion.</p>\n\n<p>Wait, what?</p>\n\n<div>\n<a  title=\"Permanent link to ‘Poll: Louisiana GOPers Unsure if Katrina Response Was Obama&#8217;s Fault’\"  href=\"http://daringfireball.net/linked/2013/08/21/louisiana-katrina\">&nbsp;★&nbsp;</a>\n</div>", :published => "2013-08-21 18:12:32", :updated => "2013-08-21 18:14:02", :created_at => "2013-08-24 12:41:15", :updated_at => "2013-08-24 12:41:15", :entry_id => "tag:daringfireball.net,2013:/linked//6.28154", :public_id => "ebb155baf90b42421af0a637e36e558dac5702e4", :old_public_id => "ebb155baf90b42421af0a637e36e558dac5702e4", :starred_entries_count => 0 },
  { :feed_id => 1, :title => "A Very Mild Defense of In-App Purchases", :url => "http://verynicewebsite.net/2013/08/a-very-mild-defense-of-in-app-purchases/", :author => "John Gruber", :summary => "Great piece by John Moltz: I come not to praise in-app purchases but to not...", :content => "<p>Great piece by John Moltz:</p>\n\n<blockquote>\n  <p>I come not to praise in-app purchases but to not bury them.</p>\n</blockquote>\n\n<div>\n<a  title=\"Permanent link to ‘A Very Mild Defense of In-App Purchases’\"  href=\"http://daringfireball.net/linked/2013/08/21/iap\">&nbsp;★&nbsp;</a>\n</div>", :published => "2013-08-21 15:23:20", :updated => "2013-08-21 15:24:44", :created_at => "2013-08-24 12:41:15", :updated_at => "2013-08-24 12:41:15", :entry_id => "tag:daringfireball.net,2013:/linked//6.28153", :public_id => "65cc9e97ee0cb9b7253307fd9c71eb94a4f26d95", :old_public_id => "65cc9e97ee0cb9b7253307fd9c71eb94a4f26d95", :starred_entries_count => 0 },
  { :feed_id => 1, :title => "The Suits of James Bond", :url => "http://thesuitsofjamesbond.com/?paged=8", :author => "John Gruber", :summary => "Matt Spaiser, aptly chronicling the sartorial details of the James Bond franchise....", :content => "<p>Matt Spaiser, aptly chronicling the sartorial details of the James Bond franchise. So great.</p>\n\n<div>\n<a  title=\"Permanent link to ‘The Suits of James Bond’\"  href=\"http://daringfireball.net/linked/2013/08/20/suits-of-james-bond\">&nbsp;★&nbsp;</a>\n</div>", :published => "2013-08-21 03:03:09", :updated => "2013-08-21 03:03:11", :created_at => "2013-08-24 12:41:15", :updated_at => "2013-08-24 12:41:15", :entry_id => "tag:daringfireball.net,2013:/linked//6.28152", :public_id => "ac7f0a05d754cc880c3458047b8f27fc88ededb0", :old_public_id => "ac7f0a05d754cc880c3458047b8f27fc88ededb0", :starred_entries_count => 0 },
  { :feed_id => 1, :title => "International Climate Panel Cites Near Certainty on Warming", :url => "http://www.nytimes.com/2013/08/20/science/earth/extremely-likely-that-human-activity-is-driving-climate-change-panel-finds.html?_r=0&#38;pagewanted=all", :author => "John Gruber", :summary => "Justin Gillis, reporting for the NYT: An international panel of scientists has...", :content => "<p>Justin Gillis, reporting for the NYT:</p>\n\n<blockquote>\n  <p>An international panel of scientists has found with near certainty\nthat human activity is the cause of most of the temperature\nincreases of recent decades, and warns that sea levels could\nconceivably rise by more than three feet by the end of the century\nif emissions continue at a runaway pace.</p>\n</blockquote>\n\n<p>See also: Chris Mooney at Mother Jones on <a href=\"http://www.motherjones.com/environment/2013/08/five-terrifying-statements-ipcc-report\">five of the &#8220;holy crap&#8221; details of the report</a>.</p>\n\n<div>\n<a  title=\"Permanent link to ‘International Climate Panel Cites Near Certainty on Warming’\"  href=\"http://daringfireball.net/linked/2013/08/20/climate\">&nbsp;★&nbsp;</a>\n</div>", :published => "2013-08-20 23:51:09", :updated => "2013-08-20 23:51:11", :created_at => "2013-08-24 12:41:15", :updated_at => "2013-08-24 12:41:15", :entry_id => "tag:daringfireball.net,2013:/linked//6.28151", :public_id => "89fa888c4a278d05bb996821ac073c383bd6c7ca", :old_public_id => "89fa888c4a278d05bb996821ac073c383bd6c7ca", :starred_entries_count => 0 },
  { :feed_id => 1, :title => "Tumult Hype 2.0 and Hype Reflect for iOS", :url => "http://blog.tumult.com/2013/08/20/introducing-tumult-hype-2-0-and-hype-reflect-for-ios-the-perfect-pair-for-creating-stunning-html5-content/", :author => "John Gruber", :summary => "Amazing graphical HTML5 animation builder, now with a helper app for previewing on...", :content => "<p>Amazing graphical HTML5 animation builder, now with a helper app for previewing on iOS devices.</p>\n\n<div>\n<a  title=\"Permanent link to ‘Tumult Hype 2.0 and Hype Reflect for iOS’\"  href=\"http://daringfireball.net/linked/2013/08/20/hype\">&nbsp;★&nbsp;</a>\n</div>", :published => "2013-08-20 22:15:34", :updated => "2013-08-20 22:15:36", :created_at => "2013-08-24 12:41:15", :updated_at => "2013-08-24 12:41:15", :entry_id => "tag:daringfireball.net,2013:/linked//6.28150", :public_id => "4d9c0879edcaa14ce715297a43a6451fb8fd553d", :old_public_id => "4d9c0879edcaa14ce715297a43a6451fb8fd553d", :starred_entries_count => 0 },
  { :feed_id => 1, :title => "Ad Age: Big Marketers Sign Up for Apple’s iTunes Radio", :url => "http://adage.com/article/digital/big-marketers-sign-apple-s-itunes-radio/243734/", :author => "John Gruber", :summary => "John McDermott, Ad Age: iTunes Radio, Apple’s answer to Pandora, is set to...", :content => "<p>John McDermott, Ad Age:</p>\n\n<blockquote>\n  <p>iTunes Radio, Apple&#8217;s answer to Pandora, is set to debut next\nmonth with a handful of high-profile brand partners including\nMcDonald&#8217;s, Nissan, Pepsi, Procter &amp; Gamble and possibly one or\ntwo more brands, according to people familiar with the\nnegotiations. […]</p>\n\n<p>Of course, consumers can choose to have no ads at all. iTunes\nRadio will be a free, ad-supported service to the public, but\nApple will be offering an ad-free option to anyone who\npurchases iTunes Match, a cloud-based music storage feature\nthat allows users to access their libraries on any\nInternet-connected Apple device.</p>\n</blockquote>\n\n<p>Best reason yet to sign up for iTunes Match.</p>\n\n<div>\n<a  title=\"Permanent link to ‘Ad Age: Big Marketers Sign Up for Apple&#8217;s iTunes Radio’\"  href=\"http://daringfireball.net/linked/2013/08/20/ad-age-itunes-radio\">&nbsp;★&nbsp;</a>\n</div>", :published => "2013-08-20 20:28:50", :updated => "2013-08-20 20:28:52", :created_at => "2013-08-24 12:41:15", :updated_at => "2013-08-24 12:41:15", :entry_id => "tag:daringfireball.net,2013:/linked//6.28149", :public_id => "cd7c37f29d4aa53adbf81fb6553a361a349e54e2", :old_public_id => "cd7c37f29d4aa53adbf81fb6553a361a349e54e2", :starred_entries_count => 0 },
  { :feed_id => 1, :title => "How Steve Jobs Got AT&T to Share Revenue", :url => "http://www.forbes.com/sites/petercohan/2013/08/16/how-steve-jobs-got-att-to-share-revenue/", :author => "John Gruber", :summary => "Peter Cohan, Forbes: When he worked at telecommunications consulting firm,...", :content => "<p>Peter Cohan, Forbes:</p>\n\n<blockquote>\n  <p>When he worked at telecommunications consulting firm, Adventis,\nRaj Aggarwal met with Apple’s Steve Jobs twice a week for several\nmonths. In an August 15 interview, Aggarwal explained how Steve\nJobs persuaded AT&amp;T’s Cingular Wireless to provide service for the\niPhone with an unprecedented revenue sharing agreement. […]</p>\n\n<p>Aggarwal was impressed by the way Jobs was willing to take a risk\nto realize his vision. “In one meeting in the conference room with\nJobs, he was annoyed that AT&amp;T was spending too much time worrying\nabout the risks of the deal. So he said, ‘You know what we should\ndo to stop them from complaining? We should write AT&amp;T a check for\n$1 billion and if the deal doesn’t work out, they can keep the\nmoney. Let’s give them the $1 billion [Apple had $5 billion in\ncash at the time] and shut them the hell up,’” Aggarwal recounted.</p>\n</blockquote>\n\n<div>\n<a  title=\"Permanent link to ‘How Steve Jobs Got AT&amp;T to Share Revenue’\"  href=\"http://daringfireball.net/linked/2013/08/20/jobs-att\">&nbsp;★&nbsp;</a>\n</div>", :published => "2013-08-20 20:06:05", :updated => "2013-08-20 20:06:07", :created_at => "2013-08-24 12:41:15", :updated_at => "2013-08-24 12:41:15", :entry_id => "tag:daringfireball.net,2013:/linked//6.28148", :public_id => "80fc7528defb8a6104e7a9e516859073057f3074", :old_public_id => "80fc7528defb8a6104e7a9e516859073057f3074", :starred_entries_count => 0 },
  { :feed_id => 1, :title => "Go Home, Samsung, You’re Drunk", :url => "http://www.theverge.com/2013/8/19/4636102/samsung-brings-largest-phone-yet-to-the-us-galaxy-mega-63", :author => "John Gruber", :summary => "Samsung to introduce 6.3-inch Galaxy Mega phone in US next month. ★", :content => "<p>Samsung to introduce 6.3-inch Galaxy Mega phone in US next month.</p>\n\n<div>\n<a  title=\"Permanent link to ‘Go Home, Samsung, You&#8217;re Drunk’\"  href=\"http://daringfireball.net/linked/2013/08/19/samsung-mega\">&nbsp;★&nbsp;</a>\n</div>", :published => "2013-08-19 19:14:16", :updated => "2013-08-19 23:56:24", :created_at => "2013-08-24 12:41:15", :updated_at => "2013-08-24 12:41:15", :entry_id => "tag:daringfireball.net,2013:/linked//6.28139", :public_id => "a7833bd64665694be3e37ed44e710be482e08a5a", :old_public_id => "a7833bd64665694be3e37ed44e710be482e08a5a", :starred_entries_count => 0 },
  { :feed_id => 1, :title => "Apple Developer Center Outage Fixed ‘Remote Code Execution’ Flaw", :url => "http://www.macrumors.com/2013/08/20/apple-developer-center-outage-fixed-remote-code-execution-flaw/", :author => "John Gruber", :summary => "Juli Clover, MacRumors: While security researcher Ibrahim Balic speculated that...", :content => "<p>Juli Clover, MacRumors:</p>\n\n<blockquote>\n  <p>While security researcher Ibrahim Balic speculated that he might\nhave been behind the security breach, it is now clear that the\nissue he reported was unrelated to the major flaw that caused the\ndowntime. Apple credits Ibrahim with reporting a separate iAd\nWorkbench vulnerability on July 22. The vulnerability allowed\nBalic to obtain both names and Apple IDs of users.</p>\n</blockquote>\n\n<p>It was a separate &#8220;remote code execution&#8221; vulnerability that prompted Apple to take the whole thing down.</p>\n\n<div>\n<a  title=\"Permanent link to ‘Apple Developer Center Outage Fixed &#8216;Remote Code Execution&#8217; Flaw’\"  href=\"http://daringfireball.net/linked/2013/08/20/adc-outage\">&nbsp;★&nbsp;</a>\n</div>", :published => "2013-08-20 20:03:00", :updated => "2013-08-20 20:03:02", :created_at => "2013-08-24 12:41:15", :updated_at => "2013-08-24 12:41:15", :entry_id => "tag:daringfireball.net,2013:/linked//6.28147", :public_id => "ea6c35ca1e780db085ad17c924e146b78d94a7cf", :old_public_id => "ea6c35ca1e780db085ad17c924e146b78d94a7cf", :starred_entries_count => 0 },
  { :feed_id => 1, :title => "Cringely on the Ashton Kutcher ‘Jobs’ Movie", :url => "http://www.cringely.com/2013/08/19/ashton-kutchers-steve-jobs-somehow-misses-the-whole-point/", :author => "John Gruber", :summary => "Cringely: For years there was this running joke that Steve had changed, that he...", :content => "<p>Cringely:</p>\n\n<blockquote>\n  <p>For years there was this running joke that Steve had changed, that\nhe was no longer that guy who made us all uncomfortable. Then an\nhour or a day later he’d do something that would show he hadn’t\nreally changed at all. And yet at some point Steve did change. It\nwas subtle but real and it set the tone for the last 15 years of\nhis life &#8212; the most productive 15 years of his life or that of\n<em>any</em> American executive.</p>\n\n<p>This film misses all of that.</p>\n</blockquote>\n\n<div>\n<a  title=\"Permanent link to ‘Cringely on the Ashton Kutcher &#8216;Jobs&#8217; Movie’\"  href=\"http://daringfireball.net/linked/2013/08/20/jobs\">&nbsp;★&nbsp;</a>\n</div>", :published => "2013-08-20 19:45:40", :updated => "2013-08-20 19:45:42", :created_at => "2013-08-24 12:41:15", :updated_at => "2013-08-24 12:41:15", :entry_id => "tag:daringfireball.net,2013:/linked//6.28146", :public_id => "cff2530289ad8ac38bab5518e32e5e440c45e405", :old_public_id => "cff2530289ad8ac38bab5518e32e5e440c45e405", :starred_entries_count => 0 },
  { :feed_id => 1, :title => "Special for the ‘Apple Would Never Make a Gold-Toned iPhone If Steve Jobs Were Still Alive’ Crowd", :url => "http://archive.techtree.com/techtree/jsp/article.jsp?article_id=55782&#38;cat_id=571", :author => "John Gruber", :summary => "Yeah, would never happen. ★", :content => "<p>Yeah, would never happen.</p>\n\n<div>\n<a  title=\"Permanent link to ‘Special for the &#8216;Apple Would Never Make a Gold-Toned iPhone If Steve Jobs Were Still Alive&#8217; Crowd’\"  href=\"http://daringfireball.net/linked/2013/08/20/gold\">&nbsp;★&nbsp;</a>\n</div>", :published => "2013-08-20 19:25:39", :updated => "2013-08-20 19:25:41", :created_at => "2013-08-24 12:41:15", :updated_at => "2013-08-24 12:41:15", :entry_id => "tag:daringfireball.net,2013:/linked//6.28145", :public_id => "2cc95d1578a7a45311015b5e3a17d189d4fb7481", :old_public_id => "2cc95d1578a7a45311015b5e3a17d189d4fb7481", :starred_entries_count => 0 },
  { :feed_id => 1, :title => "Nilay Patel Reviews the New $599 TiVo Roamio Pro", :url => "http://www.theverge.com/2013/8/20/4638390/tivo-roamio-pro-review", :author => "John Gruber", :summary => "I’m glad TiVo is still around — we’ve been TiVo users since around 1999. But a $600...", :content => "<p>I&#8217;m glad TiVo is still around &#8212; we&#8217;ve been TiVo users since around 1999. But a $600 box in 2013 that still has non-HD user interface screens? Come on.</p>\n\n<div>\n<a  title=\"Permanent link to ‘Nilay Patel Reviews the New $599 TiVo Roamio Pro’\"  href=\"http://daringfireball.net/linked/2013/08/20/tivo\">&nbsp;★&nbsp;</a>\n</div>", :published => "2013-08-20 19:17:28", :updated => "2013-08-20 19:17:30", :created_at => "2013-08-24 12:41:15", :updated_at => "2013-08-24 12:41:15", :entry_id => "tag:daringfireball.net,2013:/linked//6.28144", :public_id => "290c33095d7b48a1c0ef86f4546570f595f86e00", :old_public_id => "290c33095d7b48a1c0ef86f4546570f595f86e00", :starred_entries_count => 0 },
  { :feed_id => 1, :title => "Type Hunting", :url => "http://typehunting.com/", :author => "John Gruber", :summary => "Great collection of vintage type. (Via Kontra.) ★", :content => "<p>Great collection of vintage type. (<a href=\"https://twitter.com/counternotions/status/369635405916024832\">Via Kontra</a>.)</p>\n\n<div>\n<a  title=\"Permanent link to ‘Type Hunting’\"  href=\"http://daringfireball.net/linked/2013/08/19/type-hunting\">&nbsp;★&nbsp;</a>\n</div>", :published => "2013-08-20 01:58:14", :updated => "2013-08-20 01:58:16", :created_at => "2013-08-24 12:41:15", :updated_at => "2013-08-24 12:41:15", :entry_id => "tag:daringfireball.net,2013:/linked//6.28143", :public_id => "f72167ecb60cf6d5d8e9a3cba68124484ddc173d", :old_public_id => "f72167ecb60cf6d5d8e9a3cba68124484ddc173d", :starred_entries_count => 0 },
  { :feed_id => 1, :title => "U.K. Government Raids Guardian Office to Destroy Hard Drives", :url => "http://www.theguardian.com/commentisfree/2013/aug/19/david-miranda-schedule7-danger-reporters", :author => "John Gruber", :summary => "Guardian editor Alan Rusbridger: The state that is building such a formidable...", :content => "<p>Guardian editor Alan Rusbridger:</p>\n\n<blockquote>\n  <p>The state that is building such a formidable apparatus of\nsurveillance will do its best to prevent journalists from\nreporting on it. Most journalists can see that. But I wonder how\nmany have truly understood the absolute threat to journalism\nimplicit in the idea of total surveillance, when or if it comes &#8212;\nand, increasingly, it looks like &#8220;when&#8221;.</p>\n</blockquote>\n\n<div>\n<a  title=\"Permanent link to ‘U.K. Government Raids Guardian Office to Destroy Hard Drives’\"  href=\"http://daringfireball.net/linked/2013/08/19/guardian\">&nbsp;★&nbsp;</a>\n</div>", :published => "2013-08-20 01:56:45", :updated => "2013-08-20 03:03:01", :created_at => "2013-08-24 12:41:15", :updated_at => "2013-08-24 12:41:15", :entry_id => "tag:daringfireball.net,2013:/linked//6.28142", :public_id => "bc2a10137a021f1932d1cd8731300bc9de1237c6", :old_public_id => "bc2a10137a021f1932d1cd8731300bc9de1237c6", :starred_entries_count => 0 },
  { :feed_id => 1, :title => "Butterick’s Practical Typography", :url => "http://practicaltypography.com/", :author => "John Gruber", :summary => "New web-based book by Matthew Butterick. From his opening chapter: This is a...", :content => "<p>New web-based book by Matthew Butterick. From his opening chapter:</p>\n\n<blockquote>\n  <p>This is a bold claim, but I stand behind it: if you learn and\nfollow these five typography rules, you will be a better\ntypographer than 95% of professional writers and 70% of\nprofessional designers. (The rest of this book will raise you to\nthe 99th percentile in both categories.)</p>\n\n<p>All it takes is ten minutes &#8212; five minutes to read these rules\nonce, then five minutes to read them again.</p>\n</blockquote>\n\n<div>\n<a  title=\"Permanent link to ‘Butterick’s Practical Typography’\"  href=\"http://daringfireball.net/linked/2013/08/19/buttericks-practical-typography\">&nbsp;★&nbsp;</a>\n</div>", :published => "2013-08-20 00:21:28", :updated => "2013-08-20 00:21:29", :created_at => "2013-08-24 12:41:15", :updated_at => "2013-08-24 12:41:15", :entry_id => "tag:daringfireball.net,2013:/linked//6.28140", :public_id => "d1125cbae3652614ff3bf92ad6e57f8376871900", :old_public_id => "d1125cbae3652614ff3bf92ad6e57f8376871900", :starred_entries_count => 0 },
  { :feed_id => 1, :title => "[Sponsor] Shutterstock", :url => "http://www.shutterstock.com?pl=DFIm-WKAug19&#38;cr=images", :author => "Daring Fireball Department of Commerce", :summary => "Over 27 million stock photos, illustrations, vectors, and videos. Take a look...", :content => "<p><a href=\"http://www.shutterstock.com/?pl=DFIm-WKAug19&amp;cr=images\">Over 27 million stock photos, illustrations, vectors, and videos</a>. Take a look around. Be inspired. </p>\n\n<p>Shutterstock maintains a library of high quality royalty-free stock photos, vectors, and illustrations available by <a href=\"http://www.shutterstock.com/subscribe.mhtml?pl=DFIm-WKAug19&amp;cr=pricing\">subscription and a la carte</a>. Visitors can browse the library for free, and can license and download images through a variety of packages. The vast library covers a multitude of subjects, has over 27 million photographs, illustrations, and vectors, and stays current and diverse, with over 10,000 images added every day from photographers worldwide.</p>\n\n<p>Shutterstock&#8217;s award-winning app is available for iPhone and the iPad. </p>", :published => "2013-08-20 01:07:39", :updated => "2013-08-20 20:23:41", :created_at => "2013-08-24 12:41:15", :updated_at => "2013-08-24 12:41:15", :entry_id => "tag:daringfireball.net,2013:/feeds/sponsors//11.28141", :public_id => "130b5f32f475fa3677e2eee3299cc86218f11cce", :old_public_id => "130b5f32f475fa3677e2eee3299cc86218f11cce", :starred_entries_count => 0 },
  { :feed_id => 1, :title => "‘Why We Talk in Tongues’", :url => "http://www.nytimes.com/2013/08/18/opinion/sunday/luhrmann-why-we-talk-in-tongues.html", :author => "John Gruber", :summary => "Hard to believe The New York Times ran this piece of claptrap on their op-ed page....", :content => "<p>Hard to believe The New York Times ran this piece of claptrap on their op-ed page. &#8220;We&#8221; don&#8217;t speak in tongues; religious nutjobs do, and they do it because they believe in superstitious nonsense. I&#8217;ll bet my bottom dollar that there is a high correlation between tongue-speakers and climate change deniers and creationist &#8220;science&#8221; school curriculum pushers &#8212; people who are doing real and genuine harm to our society and the planet.</p>\n\n<p><strong>Update:</strong> As a perusal of my (and <a href=\"https://twitter.com/search?q=%40daringfireball&amp;mode=relevance&amp;src=typd\">@daringfireball</a>&#8217;s) <a href=\"https://twitter.com/search?q=%40gruber&amp;mode=relevance&amp;src=typd\">Twitter replies</a> will show, this post was, I suppose unsurprisingly, controversial. One word I&#8217;ve seen from those whom I presume to be Pentecostals or other evangelical Christians is &#8220;hate&#8221; &#8212; examples <a href=\"https://twitter.com/thomasemullin/status/369528660794933248\">here</a>, <a href=\"https://twitter.com/Tarobot/status/369623728524890112\">here</a>, <a href=\"https://twitter.com/jessehead95/status/369548461529522177\">here</a>, <a href=\"https://twitter.com/thomasqbrady/status/369573231851614209\">here</a>, <a href=\"https://twitter.com/lingthethird/status/369546107602210817\">here</a>, <a href=\"https://twitter.com/bbeckcom/status/369629203706048512\">here</a>. A lack of respect is not hatred; I do not respect superstitious nonsense. But this framing &#8212; equating lack of respect with hatred &#8212; is what keeps many from criticizing nonsensical religious views.</p>\n\n<div>\n<a  title=\"Permanent link to ‘&#8216;Why We Talk in Tongues&#8217;’\"  href=\"http://daringfireball.net/linked/2013/08/19/tongues\">&nbsp;★&nbsp;</a>\n</div>", :published => "2013-08-19 17:41:33", :updated => "2013-08-20 01:20:59", :created_at => "2013-08-24 12:41:15", :updated_at => "2013-08-24 12:41:15", :entry_id => "tag:daringfireball.net,2013:/linked//6.28138", :public_id => "ff4fa33c9e3c61ec0ff43a8f94e2d521aa81598f", :old_public_id => "ff4fa33c9e3c61ec0ff43a8f94e2d521aa81598f", :starred_entries_count => 0 },
  { :feed_id => 1, :title => "Bloomberg: ‘Samsung Said to Introduce Watch-Like Phone Next Month’", :url => "http://www.bloomberg.com/news/2013-08-16/samsung-said-to-introduce-smartphone-like-wristwatch-next-month.html", :author => "John Gruber", :summary => "Jungah Lee, reporting for Reuters: Samsung Electronics Co. will introduce a...", :content => "<p>Jungah Lee, reporting for Reuters:</p>\n\n<blockquote>\n  <p>Samsung Electronics Co. will introduce a wristwatch-like device named the Galaxy Gear next month that can make phone calls, surf the Web and handle e-mails, according to two people familiar with the matter.</p>\n\n<p>The Galaxy Gear will be powered by Google Inc.’s Android operating system and go on sale this year to beat a potentially competing product from Apple Inc., the people said. The device will be unveiled Sept. 4, two days before the IFA consumer electronics show begins in Berlin, one of the people said, asking not to be identified because the plans are private.</p>\n</blockquote>\n\n<p>A &#8220;watch-like phone&#8221; might be interesting, and for once, worthy of the &#8220;smart&#8221; moniker. Devices like the Pebble, which must be tethered to an actual smartphone for any sort of connectivity, aren&#8217;t smart at all; they&#8217;re just modern (and ultra portable) dumb terminals.</p>\n\n<p>Still though, if Samsung&#8217;s wearable is a phone, presumably the screen would be relatively small. Is this something you&#8217;re intended to replace your current phone with? If not, how do you pay for service? If it&#8217;s a true phone, it must have its own phone number, right? I&#8217;m looking forward to seeing what they&#8217;ve come up with without anything to copy.</p>\n\n<p><strong>Update:</strong> <a href=\"http://www.theverge.com/2013/8/19/4636564/samsungs-galaxy-gear-smartwatch-is-real-and-its-not-a-phone\">The Verge reports that the Gear &#8220;is not a phone&#8221;</a>; it&#8217;s just a Bluetooth accessory that connects to a phone.</p>\n\n<div>\n<a  title=\"Permanent link to ‘Bloomberg: &#8216;Samsung Said to Introduce Watch-Like Phone Next Month&#8217;’\"  href=\"http://daringfireball.net/linked/2013/08/19/samsung-watch\">&nbsp;★&nbsp;</a>\n</div>", :published => "2013-08-19 17:10:28", :updated => "2013-08-19 17:11:57", :created_at => "2013-08-24 12:41:15", :updated_at => "2013-08-24 12:41:15", :entry_id => "tag:daringfireball.net,2013:/linked//6.28134", :public_id => "ff62d0aa0b66fc64ef935354a85bf0b5f8edf4a4", :old_public_id => "ff62d0aa0b66fc64ef935354a85bf0b5f8edf4a4", :starred_entries_count => 0 },
  { :feed_id => 1, :title => "‘Amazon First Citywide Change Bank’", :url => "http://www.muleradio.net/thetalkshow/51/", :author => "John Gruber", :summary => "Speaking of MG Siegler, he’s my guest on the new episode of my podcast, The Talk...", :content => "<p>Speaking of MG Siegler, he&#8217;s my guest on the new episode of my podcast, The Talk Show. Topics include Amazon, Jeff Bezos, and what Apple might have planned for this fall.</p>\n\n<p>Brought to you by three great sponsors:</p>\n\n<ul>\n<li><p><a href=\"http://batchgeo.com/\">BatchGeo</a>: Mapping should be this easy.</p></li>\n<li><p><a href=\"http://talkshow.ting.com/\">Ting</a>: A no-BS mobile service that makes sense.</p></li>\n<li><p><a href=\"http://squarespace.com/thetalkshow\">Squarespace</a>: Everything you need to build exceptional websites.</p></li>\n</ul>\n\n<div>\n<a  title=\"Permanent link to ‘&#8216;Amazon First Citywide Change Bank&#8217;’\"  href=\"http://daringfireball.net/linked/2013/08/19/the-talk-show-052\">&nbsp;★&nbsp;</a>\n</div>", :published => "2013-08-19 16:56:33", :updated => "2013-08-19 16:56:35", :created_at => "2013-08-24 12:41:15", :updated_at => "2013-08-24 12:41:15", :entry_id => "tag:daringfireball.net,2013:/linked//6.28137", :public_id => "5fc1e7a44e94893360828c45bd0350f24f33f4f5", :old_public_id => "5fc1e7a44e94893360828c45bd0350f24f33f4f5", :starred_entries_count => 0 },
  { :feed_id => 1, :title => "Goldfinger: The Next iPhone", :url => "http://techcrunch.com/2013/08/18/gold-iphone/", :author => "John Gruber", :summary => "MG Siegler: Yes, there will be a gold iPhone. That’s the latest I’m hearing...", :content => "<p>MG Siegler:</p>\n\n<blockquote>\n  <p>Yes, there will be a gold iPhone.</p>\n\n<p>That’s the latest I’m hearing from multiple sources after several weeks of rumors and possible component leaks suggesting the same thing. At first, I couldn’t believe Apple would break from the tradition of offering the simple choice: black and white (or “slate” and “silver” if you prefer for the iPhone 5) for their flagship device. Gold simply seemed too gaudy, perhaps even tacky. But a few compelling arguments countered my disbelief. And now, upon checking, sure enough, there will be gold.</p>\n</blockquote>\n\n<div>\n<a  title=\"Permanent link to ‘Goldfinger: The Next iPhone’\"  href=\"http://daringfireball.net/linked/2013/08/19/siegler\">&nbsp;★&nbsp;</a>\n</div>", :published => "2013-08-19 16:38:14", :updated => "2013-08-19 16:41:28", :created_at => "2013-08-24 12:41:15", :updated_at => "2013-08-24 12:41:15", :entry_id => "tag:daringfireball.net,2013:/linked//6.28136", :public_id => "f19f604c438dd4b53af9da6a642549ff6fcf18d7", :old_public_id => "f19f604c438dd4b53af9da6a642549ff6fcf18d7", :starred_entries_count => 0 },
  { :feed_id => 1, :title => "The Gold iPhone 5S", :url => "http://www.imore.com/gold-iphone-5s", :author => "John Gruber", :summary => "Rene Ritchie: So, technologically, it wouldn’t be hard for Apple to make a gold...", :content => "<p>Rene Ritchie:</p>\n\n<blockquote>\n  <p>So, technologically, it wouldn&#8217;t be hard for Apple to make a gold iPhone. But commercially, what would be the motivation?</p>\n\n<p>Given how popular gold is as an aftermarket option for color-treatments, and how many gold cases there are &#8212; including but certainly not limited to the Asian markets &#8212; it could simply be the decision to offer supply where there&#8217;s demand.</p>\n</blockquote>\n\n<p>Can&#8217;t say I&#8217;m a fan of gold, but it&#8217;s clear many people are.</p>\n\n<div>\n<a  title=\"Permanent link to ‘The Gold iPhone 5S’\"  href=\"http://daringfireball.net/linked/2013/08/19/goldenfive\">&nbsp;★&nbsp;</a>\n</div>", :published => "2013-08-19 16:21:49", :updated => "2013-08-19 16:25:08", :created_at => "2013-08-24 12:41:15", :updated_at => "2013-08-24 12:41:15", :entry_id => "tag:daringfireball.net,2013:/linked//6.28135", :public_id => "d261eb3afaf9ae73595b2796c93aa31b6922394a", :old_public_id => "d261eb3afaf9ae73595b2796c93aa31b6922394a", :starred_entries_count => 0 },
  { :feed_id => 1, :title => "Why We Want TV to Be Disrupted So Badly", :url => "http://techland.time.com/2013/08/19/why-we-want-tv-to-be-disrupted-so-badly/", :author => "John Gruber", :summary => "Ben Bajarin, writing for Time Techland: Those who have the most to lose when TV...", :content => "<p>Ben Bajarin, writing for Time Techland:</p>\n\n<blockquote>\n  <p>Those who have the most to lose when TV gets disrupted need not fear piracy; they should fear the SDK. When developers can take advantage of a platform, the possibilities are endless.</p>\n</blockquote>\n\n<p>Which is why Apple should be seen as the frontrunner. It&#8217;s exactly what happened to the mobile phone industry.</p>\n\n<div>\n<a  title=\"Permanent link to ‘Why We Want TV to Be Disrupted So Badly’\"  href=\"http://daringfireball.net/linked/2013/08/19/bajarin-tv\">&nbsp;★&nbsp;</a>\n</div>", :published => "2013-08-19 14:13:26", :updated => "2013-08-19 14:18:13", :created_at => "2013-08-24 12:41:15", :updated_at => "2013-08-24 12:41:15", :entry_id => "tag:daringfireball.net,2013:/linked//6.28133", :public_id => "cda8dff7b216c9c5ab0b521767636d979c62ce36", :old_public_id => "cda8dff7b216c9c5ab0b521767636d979c62ce36", :starred_entries_count => 0 },
  { :feed_id => 1, :title => "Jeff Bezos, Unexpected Newspaper Owner", :url => "http://www.nytimes.com/2013/08/18/business/expecting-the-unexpected-from-jeff-bezos.html?ref=business&#38;_r=1&#38;pagewanted=all&#38;", :author => "John Gruber", :summary => "David Streitfeld and Christine Haughney, writing for the NYT: “Every story you...", :content => "<p>David Streitfeld and Christine Haughney, writing for the NYT:</p>\n\n<blockquote>\n  <p>“Every story you ever see about Amazon, it has that sentence: ‘An Amazon spokesman declined to comment,’ “ Mr. Marcus said.</p>\n\n<p>Drew Herdener, an Amazon spokesman, declined to comment.</p>\n</blockquote>\n\n<div>\n<a  title=\"Permanent link to ‘Jeff Bezos, Unexpected Newspaper Owner ’\"  href=\"http://daringfireball.net/linked/2013/08/18/bezos\">&nbsp;★&nbsp;</a>\n</div>", :published => "2013-08-18 16:56:29", :updated => "2013-08-18 17:05:20", :created_at => "2013-08-24 12:41:15", :updated_at => "2013-08-24 12:41:15", :entry_id => "tag:daringfireball.net,2013:/linked//6.28132", :public_id => "026ae88fd47e630ddd228fcc6039900956e8fb0d", :old_public_id => "026ae88fd47e630ddd228fcc6039900956e8fb0d", :starred_entries_count => 0 },
  { :feed_id => 2, :title => "Tag Management", :url => "http://blog.feedbin.me/2013/07/19/tag-management/", :author => nil, :summary => "You can now rename and delete tags in Feedbin.", :content => "<img src='/assets/images/2013-07-19/tag-management.gif' style='max-width: 896px;' />\n<p>You can now rename and delete tags in Feedbin.</p>", :published => "2013-07-19 07:00:00", :updated => nil, :created_at => "2013-08-24 12:46:51", :updated_at => "2013-08-24 12:46:51", :entry_id => "http://blog.feedbin.me/2013/07/19/tag-management", :public_id => "ccea6a4e51b35ed59eb091d5390da81ecec5fc1c", :old_public_id => "ccea6a4e51b35ed59eb091d5390da81ecec5fc1c", :starred_entries_count => 0 },
  { :feed_id => 1, :title => "Apple Touting New Mac Pro in Movie Theater Teaser", :url => "http://www.macrumors.com/2013/08/16/apple-begins-showing-mac-pro-teaser-in-movie-theaters-with-coming-fall-2013-announcement/", :author => "John Gruber", :summary => "Impressive marketing move for a product that is by all means truly “pro”. ★", :content => "<p>Impressive marketing move for a product that is by all means truly &#8220;pro&#8221;.</p>\n\n<div>\n<a  title=\"Permanent link to ‘Apple Touting New Mac Pro in Movie Theater Teaser’\"  href=\"http://daringfireball.net/linked/2013/08/18/mac-pro\">&nbsp;★&nbsp;</a>\n</div>", :published => "2013-08-18 16:04:26", :updated => "2013-08-18 16:04:27", :created_at => "2013-08-24 12:41:15", :updated_at => "2013-08-24 12:41:15", :entry_id => "tag:daringfireball.net,2013:/linked//6.28131", :public_id => "3fd25c1d33730f348c9de52a8f08309d726f1b96", :old_public_id => "3fd25c1d33730f348c9de52a8f08309d726f1b96", :starred_entries_count => 0 },
  { :feed_id => 1, :title => "iDraw", :url => "http://www.indeeo.com/idraw/", :author => "John Gruber", :summary => "My thanks to Indeeo for sponsoring this week’s DF RSS feed to promote iDraw, their...", :content => "<p>My thanks to Indeeo for sponsoring this week&#8217;s DF RSS feed to promote iDraw, their amazing vector illustration app for Mac OS X. iDraw is lightweight, modern, and fast. It can import and edit a variety of formats &#8212; including PDF, SVG, EPS, and AI files &#8212; and the latest update adds the ability to import and export Photoshop PSD files. Unlike many other apps which import PSD files, iDraw can also import the vector paths from Shape Layers and import Layer Styles as editable drop shadows, glows, etc. rather than simple flat rasterized layers. One use case for this: interface and web designers can import resources from sites like <a href=\"http://www.365psd.com/\">365psd</a> or <a href=\"http://designmoo.com/\">Designmoo</a> directly into iDraw. iDraw is my new go-to vector editing app.</p>\n\n<p>If that&#8217;s not enough, there&#8217;s also an <a href=\"http://www.indeeo.com/idraw/ipad.html\">iPad version of iDraw</a>, and documents can be shared between Mac and iPad using iCloud. <a href=\"http://www.indeeo.com/idraw/buy/\">Buy iDraw today on the Mac App Store for just $24.99</a>.</p>\n\n<div>\n<a  title=\"Permanent link to ‘iDraw’\"  href=\"http://daringfireball.net/linked/2013/08/17/idraw\">&nbsp;★&nbsp;</a>\n</div>", :published => "2013-08-17 16:03:15", :updated => "2013-08-17 16:03:17", :created_at => "2013-08-24 12:41:15", :updated_at => "2013-08-24 12:41:15", :entry_id => "tag:daringfireball.net,2013:/linked//6.28130", :public_id => "245b3b7410673fd6241259209bf556042fa44e10", :old_public_id => "245b3b7410673fd6241259209bf556042fa44e10", :starred_entries_count => 0 },
  { :feed_id => 1, :title => "The Shining and the Steadicam", :url => "http://www.tested.com/art/movies/457145-shining-and-steadicam/", :author => "John Gruber", :summary => "David Konow, writing for Tested: When Brown showed Kubrick that the Steadicam...", :content => "<p>David Konow, writing for Tested:</p>\n\n<blockquote>\n  <p>When Brown showed Kubrick that the Steadicam could shoot at a lens height from eighteen inches to waist-high, Kubrick was thrilled because much of the film revolved around a kid’s point of view. To film little Danny riding his Big Wheel through the halls, Brown rode on a wheelchair that Kubrick used for A Clockwork Orange.</p>\n\n<p>Brown used the wheelchair because “in a number of instances it was the only way to get the lens right down to floor level.” Brown tried to follow Danny on foot, and got tired after three minutes. “I never even tried running after the kid, that would’ve been a joke,” Brown says. “A kid on a Big Wheel can go about seventy miles an hour.”</p>\n</blockquote>\n\n<div>\n<a  title=\"Permanent link to ‘The Shining and the Steadicam’\"  href=\"http://daringfireball.net/linked/2013/08/17/shining-steadicam\">&nbsp;★&nbsp;</a>\n</div>", :published => "2013-08-17 14:51:55", :updated => "2013-08-17 14:54:46", :created_at => "2013-08-24 12:41:15", :updated_at => "2013-08-24 12:41:15", :entry_id => "tag:daringfireball.net,2013:/linked//6.28129", :public_id => "42825c59e63d131e5e67ae76c6b628fa886d7e35", :old_public_id => "42825c59e63d131e5e67ae76c6b628fa886d7e35", :starred_entries_count => 0 },
  { :feed_id => 1, :title => "Halo and Gears of War Writer Joins Amazon Games Studios", :url => "http://www.eurogamer.net/articles/2013-08-16-halo-and-gears-of-war-writer-joins-amazon-games-studios", :author => "John Gruber", :summary => "Jeffrey Matulef, reporting for Eurogamer: Sci-fi author and former writer and...", :content => "<p>Jeffrey Matulef, reporting for Eurogamer:</p>\n\n<blockquote>\n  <p>Sci-fi author and former writer and story consultant for Microsoft Game Studios, Eric Nylund, has joined up with Amazon Game Studios as its new director of narrative design.</p>\n</blockquote>\n\n<p>Interesting.</p>\n\n<div>\n<a  title=\"Permanent link to ‘Halo and Gears of War Writer Joins Amazon Games Studios’\"  href=\"http://daringfireball.net/linked/2013/08/16/amazon-games\">&nbsp;★&nbsp;</a>\n</div>", :published => "2013-08-16 22:32:21", :updated => "2013-08-16 23:32:06", :created_at => "2013-08-24 12:41:15", :updated_at => "2013-08-24 12:41:15", :entry_id => "tag:daringfireball.net,2013:/linked//6.28128", :public_id => "bf018903ad850585a85b10ac24d83a4171bff810", :old_public_id => "bf018903ad850585a85b10ac24d83a4171bff810", :starred_entries_count => 0 },
  { :feed_id => 1, :title => "Microsoft on Google’s Blocking of Their YouTube App", :url => "http://blogs.technet.com/b/microsoft_on_the_issues/archive/2013/08/15/the-limits-of-google-s-openness.aspx", :author => "John Gruber", :summary => "David Howard, corporate attorney for Microsoft: We think it’s clear that Google...", :content => "<p>David Howard, corporate attorney for Microsoft:</p>\n\n<blockquote>\n  <p>We think it’s clear that Google just doesn’t want Windows Phone users to have the same experience as Android and Apple users, and that their objections are nothing other than excuses. Nonetheless, we are committed to giving our users the experience they deserve, and are happy to work with Google to solve any legitimate concerns they may have. In the meantime, we once again request that Google stop blocking our YouTube app.</p>\n</blockquote>\n\n<p>Open always wins.</p>\n\n<div>\n<a  title=\"Permanent link to ‘Microsoft on Google&#8217;s Blocking of Their YouTube App’\"  href=\"http://daringfireball.net/linked/2013/08/16/msft-youtube\">&nbsp;★&nbsp;</a>\n</div>", :published => "2013-08-16 16:12:51", :updated => "2013-08-16 16:14:51", :created_at => "2013-08-24 12:41:15", :updated_at => "2013-08-24 12:41:15", :entry_id => "tag:daringfireball.net,2013:/linked//6.28127", :public_id => "e9d9bedda6b5027a6f55662b071d48c104fb145f", :old_public_id => "e9d9bedda6b5027a6f55662b071d48c104fb145f", :starred_entries_count => 0 },
